package gotrax

import (
	"crypto/rand"
	"encoding/hex"
	"errors"
	"io"
)

// ErrShortRandomRead is returned from certain functions when trying to read too much randomness
// and not enough is available
var ErrShortRandomRead = errors.New("short read from random source")

// WithRandom exposes randomness of a type
type WithRandom interface {
	RandReader() io.Reader
}

// RandomInto will fill the buffer with random data, or return an error if not enough randomness is available
func RandomInto(r WithRandom, b []byte) error {
	if _, err := io.ReadFull(r.RandReader(), b); err != nil {
		return ErrShortRandomRead
	}
	return nil
}

type readerWithRandom struct {
	r io.Reader
}

func (r *readerWithRandom) RandReader() io.Reader {
	return r.r
}

// ReaderIntoWithRandom will turn a regular io.Reader into a WithRandom
func ReaderIntoWithRandom(r io.Reader) WithRandom {
	wr, ok := r.(WithRandom)
	if ok {
		return wr
	}

	return &readerWithRandom{r: r}
}

// DefaultRandom returns the system default randomness as a WithRandom
func DefaultRandom() WithRandom {
	return ReaderIntoWithRandom(rand.Reader)
}

// RandomUint32 will read 4 bytes from the randomness source and interpret that as a random uint32
func RandomUint32(w WithRandom) uint32 {
	b := [4]byte{}
	RandomInto(w, b[:])
	_, ww, _ := ExtractWord(b[:])
	return ww
}

type fixedRandBytesReader struct {
	data [][]byte
	at   int
}

// FixedRand returns a random reader with the strings given, interpreted as hexadecimal bytes
func FixedRand(data []string) io.Reader {
	res := make([][]byte, len(data))
	for ix, v := range data {
		res[ix] = []byte(BytesFromHex(v))
	}

	return &fixedRandBytesReader{res, 0}
}

// FixedRandBytes returns a WithRandom initialized with the byte arrays given
func FixedRandBytes(data ...[]byte) WithRandom {
	return &fixedRandBytesReader{data, 0}
}

// BytesFromHex interprets the string as a stream of bytes. Errors are ignored.
func BytesFromHex(s string) []byte {
	val, _ := hex.DecodeString(s)
	return val
}

func (frr *fixedRandBytesReader) Read(p []byte) (n int, err error) {
	if frr.at >= len(frr.data) {
		frr.at = 0
	}
	plainBytes := frr.data[frr.at]
	frr.at++
	n = copy(p, plainBytes)
	return
}

func (frr *fixedRandBytesReader) RandReader() io.Reader {
	return frr
}

// FixtureRand returns a reader that is initialized with fixture data
func FixtureRand() io.Reader {
	return FixedRand(randData)
}

